import { Suspense } from 'react';
import {useLayoutEffect, useState} from 'react';
import './App.css';

function App()  {

  const [newItem, setNewItem] = useState("")
  const [todo, setTodo] = useState([])

// prevente a pagina to refresh
  function handleSubmit(e) {
    e.preventDefault()

    setTodo(currentTodo => {
      return[
        ...currentTodo,
        { id: crypto.randomUUID(), title: newItem, completed: false},
      ]
    })
    setNewItem("")

    }
    function toggleTodos(id, completed) {
      setTodo(currentTodo => {
        return currentTodo.map(todos => {
          if (todos.id === id){
            return {...todos, completed}
          }
        })
      })


    }

 return (
   <>

   <form  onSubmit={handleSubmit} className='include-item-form'> 
  <div className='title-row'>
    <label htmlFor='name'>Include Item</label>
    <input type='text' value={newItem} onChange={e => setNewItem(e.target.value)} id='item' placeholder="Add your Item" />
  </div>
    <button className='btn'>Add Item</button>
  </form>
  <h1 className='header'> To Do </h1>
  <ul classNmae='list'>
  {todo.map(todos => {
    return <li key={todos.id}>
      <label>
        <input type="checkbox" checked={todos.completed} onChange={e => toggleTodos(todos.id, e.target.checked)}/>
        {todos.title}
      </label>
      <button className="btn-del"> Delete</button>
    </li>
  })}
  </ul>

  </>
  );
}

export default App;
