function App()  {

  const [newItem, setNewItem] = useState("")
  const [todo, setTodo] = useState([]);

// prevente a pagina to refresh
  function handleSubmit(e) {
    e.preventDefault()

    setTodo(currentTodo => {
      return[
        ...currentTodo,
        { id:crypto.randonUIUID(), title: newItem, complete: false},
      ]
    })

    }




 return(
   <>

   <form  onSubmit={handleSubmit} className='include-item-form'> 
  <div className='title-row'>
    <label htmlFor='name'>Include Item</label>
    <input type='text' value={newItem} onChange={e => setNewItem(e.target.value)} id='item' placeholder="Add your Item" />
  </div>
    <button className='btn'>Add Item</button>
  </form>
  <h1 className='header'> List </h1>
  <ul>
    <li>
      <label>
        <input type="checkbox" />
        Item 1
      </label>
      <button className="btn-del"> Delete</button>
    </li>
  </ul>

  </>
  );
}

export default App;
