      setTodo(currentTodo => {
        return currentTodo.map(todos => {
          if (todos.id === id){
            return {...todos, completed}
          }
          return todos
        })
      })


    }

    function deleteTodos(id) {
      setTodo(currentTodo => {
        return currentTodo.filter(todos => todos.id !== id)
      }) 
    }

 return (
   <>
   <IncludeItemForm onSubmit={addTodo}/>


  <h1 className='header'> ToDo List Below </h1>
  <ul classNmae='list'>
  {todo.length === 0 && "No ToDo "}
  {todo.map(todos => {
    return <li key={todos.id}>
      <label>
        <input type="checkbox" checked={todos.completed} onChange={e => toggleTodos(todos.id, e.target.checked)}/>
        {todos.title}
      </label>
      <button onClick={() => deleteTodos(todos.id)}      className="btn-del"> Delete</button>
    </li>
  })}
  </ul>

  </>
  );
}

export default App;
